/**
 * Obtiene proveedor de registro desde `userName`
 * @param {String} userName Nombre del usuario desde Cognito
 */
const getProviderFromUserName = (userName) => {
    return userName.includes('@') ? 'Cognito' : userName.split('_')[0]
}

module.exports.getProviderFromUserName = getProviderFromUserName